import React from "react";



class Form extends React.Component{
    getPosition = ()=>{
        navigator.geolocation.getCurrentPosition((pos)=>{
            const lat = pos.coords.latitude;
            const lon = pos.coords.longitude;
            //console.log(lat,lon)
        })
    }

    render(){
        return (
            <form onSubmit= {this.props.wetherMetod}>
                <p>
                  <select size="3" multiple name="exclude">
                    <option disabled>не показывать</option>
                    <option value="current">Текущую</option>
                    <option value="minutely">По минутно</option>
                    <option value="hourly">Почасовую</option>
                    <option value="daily">Ежедную</option>
                  </select>
                </p>

                <button> определить место положение </button>
            </form>
        );
    }
}

export default Form
