import React from "react";
import Mean from "./components/mean";
import Form from "./components/form";
import Wethe_aut from "./components/wethe _autt";

const API_key = "0ba9122c7db41155430fb755e75d71e0";



class App extends React.Component {
  state={
    temp:undefined,
    city:undefined,
    pressure: undefined,
    error:undefined

  }

  getPosition = ()=>{
    navigator.geolocation.getCurrentPosition((pos)=>{
       const lat= pos.coords.latitude
       const lon= pos.coords.longitude
       console.log(lat,lon)

    })
}
  gettingWether = async(e,lat,lon)=>{
    e.preventDefault();
    const exclude = e.target.elements.exclude.value;
    const city = e.target.elements.city.value;
    const city_lat = city[0]
    const lot = e.target.elements.city.lot;

    //const lon = ;

    //if(exclude === "minutely"){
    const api_url = await
    fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=51.063931399999994&lon=16.9773222&exclude=${exclude}&appid=${API_key}&lang=ru&units=metric`)
    const date = await api_url.json()
    console.log(exclude)
    console.log(city,lot,city_lat)



    this.setState({
      temp:date.current.temp,
      city:date.timezone,
      pressure:date.current.pressure,
      error:""


    })

  }
  render(){
    return(
      <React.Fragment>
        <Mean/>
        <Form 
        wetherMetod = {this.gettingWether}
        positionMetod = {this.getPosition}/>
        <Wethe_aut
        temp={this.state.temp}
        city={this.state.city}
        pressure={this.state.pressure}
        error  = {this.state.error}
        />
      </React.Fragment>



    );
  }
}

export default App
