import React from "react";
import Mean from "./components/mean";
import Form from "./components/form";
import Wethe_aut from "./components/wethe _autt";

const API_key = "0ba9122c7db41155430fb755e75d71e0";



class App extends React.Component {
  state={
    temp:undefined,
    city:undefined,
    pressure: undefined,
    error:undefined

  }

  getPosition = ()=>{
    navigator.geolocation.getCurrentPosition((pos)=>{
       const lat= pos.coords.latitude
        const lon= pos.coords.longitude
    })
}
  gettingWether = async(e,lat,lon)=>{
    e.preventDefault();
    const exclude = e.target.elements.exclude.value;
    //const lon = ;
    const api_url = await
    fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=-94.04&exclude=${exclude}&appid=${API_key}`)
    const date = await api_url.json()
    console.log(date)
    console.log(exclude)
    console.log(lat,lon)


    this.setState({
      temp:date.current.temp,
      city:date.timezone,
      pressure:date.pressure


    })

  }
  render(){
    return(
      <React.Fragment>
        <Mean/>
        <Form 
        wetherMetod = {this.gettingWether}
        positionMetod = {this.getPosition}/>
        <Wethe_aut/>
      </React.Fragment>



    );
  }
}

export default App
